import React, { useState } from 'react';

function PatientForm() {
  const [regPhone, setRegPhone] = useState('');
  const [binNumber, setBinNumber] = useState('');
  const [lookupPhone, setLookupPhone] = useState('');
  const [result, setResult] = useState(null);

  const handleRegister = async (e) => {
    e.preventDefault();
    const res = await fetch('http://localhost:5000/api/patient/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ phoneNumber: regPhone, binNumber }),
    });
    const data = await res.json();
    setResult(data);
  };

  const handleLookup = async (e) => {
    e.preventDefault();
    const res = await fetch(`http://localhost:5000/api/patient/lookup/${lookupPhone}`);
    const data = await res.json();
    setResult(data);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Register Patient</h2>
      <form onSubmit={handleRegister}>
        <input
          type="text"
          placeholder="Phone Number"
          value={regPhone}
          onChange={(e) => setRegPhone(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Bin Number"
          value={binNumber}
          onChange={(e) => setBinNumber(e.target.value)}
          required
        />
        <button type="submit">Register</button>
      </form>

      <h2>Lookup Patient Bin</h2>
      <form onSubmit={handleLookup}>
        <input
          type="text"
          placeholder="Phone Number"
          value={lookupPhone}
          onChange={(e) => setLookupPhone(e.target.value)}
          required
        />
        <button type="submit">Lookup</button>
      </form>

      <h3>Result:</h3>
      <pre>{JSON.stringify(result, null, 2)}</pre>
    </div>
  );
}

export default PatientForm;
